import React, { Component } from 'react';
import { add, minus } from '../store/counter';
import { connect } from 'react-redux';
import './BarDemo.css';


// use connect to patch the actions and state
@connect(
    state => ({ num: state.counter }),
    {
        add,
        minus,
    }
)

class BarDemo extends Component {
    // add progress bars
    ProgressBar = (props) => {
    return (
      <div className="progress-container">
        <div
          className="progress-bar"
          style={{width: `${props.num}%`}}
         ></div>
      </div>
    );
}

    render() {
        return (
             <div>
                {this.props.num}
                <ProgressBar />
                <button onClick={() => this.props.add(20)}>+20</button>
                <button onClick={() => this.props.add(10)}>+10</button>
                <button onClick={() => this.props.minus(10)}>-10</button>
                <button onClick={() => this.props.minus(20)}>-20</button>
             </div>
        );
    }
}

export default BarDemo;
